import axios from 'axios';

// Action types
const READ = 'spaceTravel/missions/READ';

// Action creators
export const read = (teams) => ({
  type: READ,
  payload: teams,
});

// Reducer
const teamssReducer = (state = [], action) => {
  switch (action.type) {
    case READ:
      return action.payload;
    default:
      return state;
  }
};

export const recieveLeagues = () => async () => {

  let leagues = [];
  await axios.get('https://football-apis.herokuapp.com/api/v1/leagues')
    .then((response) => {
      leagues = response.data.leagues;
      console.log(leagues);
    });
};

export default teamssReducer;
