import React from "react";
import { useState } from 'react';
import Team from "./team";

function Leagues (leagues) {
  const leagueList = leagues.leagues;
  const [teamsList, setTeamsList] = useState(leagueList.filter((item) => item.id === '423e4faa-4c4b-4cf2-b1e9-03e8e5cace1f')[0]);

  console.log('leagueList =', leagueList);
  console.log('teamsList =', teamsList);

  const chooseLeague = (id) => {
    console.log('chose', id);
    setTeamsList(() => leagueList.filter((item) => item.id === id)[0].teams)
    // setTeamsList(() => leagueList.filter((item) => item.id === '423e4faa-4c4b-4cf2-b1e9-03e8e5cace1f'))
    console.log('changed to', teamsList);
  }

  return (
    <section>
      <div className="navigator">
        {/* {leagueList.map((item) => (
          <button onClick={() => {chooseLeague(item.id)}}>
          <img  src={item.logo} style={ { width: '50px', height: '60px' } } alt="League logo"/>
        </button>
        ))} */}
        <button type="button" onClick={() => chooseLeague('423e4faa-4c4b-4cf2-b1e9-03e8e5cace1f')}>
          Premier League
        </button>
        <button type="button" onClick={() => chooseLeague('1171c526-3c18-44f9-83b1-c7c470e0dd8c')}>
          Serie A
        </button>
        <button type="button" onClick={() => chooseLeague('1631784c-dbcf-4942-8be0-69534b654f8a')}>
          La Liga
        </button>
        <button type="button" onClick={() => chooseLeague('140cd7fb-0598-425d-8616-8efe12f0e5f3')}>
          Primeira Liga
        </button>
      </div>

      <div>
        {teamsList.map((team) => (
          <Team key={team.id} id={team.id} name={team.name} details={team.details}
        founded={team.founded} logo={team.logo} city={team.city} />
        ))}
      </div>
    </section>
  );
}

export default Leagues;
