import React from "react";
import { useSelector, useDispatch } from 'react-redux';
import { useEffect, useState } from 'react';
import Team from "./team";

function Leagues () {
  const leagueList = useSelector(state => state);
  // let teamsList = [];
  const [teams, setTeams] = useState(leagueList.filter((l) => l.id === l.name === 'Premier League')[0]);
  // if(leagueList.length !== 0)  teamsList = leagueList[0].teams;

  console.log('at teams leagueList =', leagueList);
  console.log('teamsList =', teams);

  const chooseLeague = (id) => {
    console.log('chose', id);
    // teams = leagueList.filter((l) => l.id === id)[0];
    setTeams(() => leagueList.filter((l) => l.id === id)[0])
    console.log('changed to', teams);
  }

  return (
    <section>
      <div className="navigator">
        {leagueList.map((item) => (
          <button onClick={() => {chooseLeague(item.id)}}>
          <img  src={item.logo} style={ { width: '50px', height: '60px' } } alt="League logo"/>
        </button>
        ))}
      </div>

      <div>
        {teams.map((team) => (
          <Team key={team.id} id={team.id} name={team.name} details={team.details}
        founded={team.founded} logo={team.logo} />
        ))}
      </div>
    </section>
  );
}

export default Leagues;
