import React, { useState } from "react";
import { recieveLeagues } from "../redux/teams";
import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Team from "./team";

function Leagues () {
  const leagueList = useSelector(state => state);
  const [teamsList, setTeamsList] = useState(leagueList.filter((league) => league.id === '423e4faa-4c4b-4cf2-b1e9-03e8e5cace1f')[0]);

  const dispatch = useDispatch();

  useEffect(() => async () => {
    await dispatch(recieveLeagues());
  }, []);

  const showLeague = (id) => {
    setTeamsList(() => leagueList.filter((league) => league.id === id)[0]);
  };

  console.log('leagueList =', leagueList);
  console.log('teamsList =', teamsList);

  return (
    <section>
      <div className="navigator">
          <button>
          {/* <img  src={leagueList[0].logo} style={ { width: '50px', height: '60px' } } alt="League logo"/> */}
        </button>
      </div>

      <div>
        {teamsList.map((team) => (
          <Team key={team.id} id={team.id} name={team.name} details={team.details}
        founded={team.founded}  city={team.city} logo={team.logo} />
        ))}
      </div>
    </section>
  );
}

export default Leagues;
